#!/usr/bin/env python3

import re
import sys

def get_order(inconn):
    out = []
    for l in inconn:
        sl = l.rstrip('\n').split('\t')
        out.append(sl[4])
    return(out)

def reconf(inconn, order):
    out = []
    ordre = re.compile(r"^contig_order_phumanus=")
    for l in inconn:
        l = l.rstrip('\n')
        if not ordre.match(l):
            out.append(l)
        else:
            prefix = "contig_order_phumanus="
            line = prefix + ",".join(order)
            out.append(line)
    return(out)

def write_conf(conf):
    for l in conf:
        print(l)

def main():
    with open(sys.argv[1], "r") as inconn:
        order = get_order(inconn)
    with open(sys.argv[2], "r") as inconn:
        conf = reconf(inconn, order)
    write_conf(conf)

if __name__ == "__main__":
    main()

